class UserInfo:
    def __init__(self, username, password):
        self.username = username
        self._password = password  # conventionally "protected" attribute

    def get_info(self, attribute_name):
        # unsafe attribute access based on user input
        return getattr(self, attribute_name, "Attribute not found")

user = UserInfo("Alice", "secret123")

# user input
user_input = sys.argv[1]          # assume a malicious entry "_password" ..

print(user.get_info(user_input))  # .. this will print "secret123", exposing the password!
