Remediation for Sample 2:

@Entity
@Table(name = "employees")
@Inheritance(strategy=InheritanceType.JOINED) // Fixed with table per subclass strategy
public class Employee {
    @Id
    @GeneratedValue(strategy = GenerationType.AUTO)
    @Column(name = "empid")
    private int empid;

    @Column(name = "name")
    private String name;

    @Column(name = "designation")
    private String designation;

    @Column(name = "emailid")
    private String emailId;
}

@Entity
@Table(name = "managers")
@PrimaryKeyJoinColumn(name = "empid")
public class Manager extends Employee
{
    @Column(name = "reportee_count")
    private int reporteeCount;

    @Column(name = "mgmt_experience")
    private float mgmtExperience;

    @Column(name = "max_reportees")
    private int maxReportees;
}

