@Override
public void doPost(HttpServletRequest request, HttpServletResponse response) throws ServletException, IOException, NoSuchAlgorithmException {
    response.setContentType("text/html;charset=UTF-8");

    float rand = java.security.SecureRandom.getInstance("SHA1PRNG").nextFloat();    // Use of SecureRandom here
    String rememberMeKey = Float.toString(rand).substring(2); // Trim off the 0. at the front.

    String user = "Floyd";
    String fullClassName = this.getClass().getName();
    String testCaseNumber = fullClassName.substring(fullClassName.lastIndexOf('.')+1+"BenchmarkTest".length());
    user+= testCaseNumber;

    String cookieName = "rememberMe" + testCaseNumber;

    boolean foundUser = false;
    javax.servlet.http.Cookie[] cookies = request.getCookies();
    if (cookies != null) {
        for (int i = 0; !foundUser && i < cookies.length; i++) {
            javax.servlet.http.Cookie cookie = cookies[i];
            if (cookieName.equals(cookie.getName())) {
                if (cookie.getValue().equals(request.getSession().getAttribute(cookieName))) {
                    foundUser = true;
                }
            }
        }
    }

    if (foundUser) {
        response.getWriter().println("Welcome back: " + user + "<br/>");
    } else {            
        javax.servlet.http.Cookie rememberMe = new javax.servlet.http.Cookie(cookieName, rememberMeKey);
        rememberMe.setSecure(true);
        rememberMe.setHttpOnly(true);
        rememberMe.setDomain(new java.net.URL(request.getRequestURL().toString()).getHost());
        rememberMe.setPath(request.getRequestURI()); // i.e., set path to JUST this servlet 
                                                     // e.g., /benchmark/sql-01/BenchmarkTest01001
        request.getSession().setAttribute(cookieName, rememberMeKey);
        response.addCookie(rememberMe);
        response.getWriter().println(
            user + " has been remembered with cookie: " + rememberMe.getName() 
                + " whose value is: " + rememberMe.getValue() + "<br/>"
        );
    }

    response.getWriter().println("Weak Randomness Test java.util.Random.nextFloat() executed");
}

