private string GetUserAddress(int userId)
{
    string userAddress = "";
    string userName = GetUserName(userId);
    string sql = "SELECT [UserAddress] FROM [AppUsers] WHERE [UserName] = '" + userName + "' ";
    using (SqlConnection connexion = new SqlConnection())
    {
        connexion.Open();
        using (SqlCommand command = new SqlCommand(sql, connexion))
        {
            using (SqlDataReader reader = command.ExecuteReader())
            {
                if (reader.Read())
                {
                    userAddress = reader.GetString(0);
                }
            }
        }
    }
    return userAddress;
}
